package com.sri.biospice.warehouse.schema;

import com.sri.biospice.warehouse.WarehouseFunctionalTest;
import com.sri.biospice.warehouse.schema.object.NucleicAcid;
import com.sri.biospice.warehouse.schema.object.SubSequence;
import java.sql.SQLException;

/**
 * @author Valerie.Wagner@sri.com
 *         Date: Aug 18, 2004
 */
public class SubSequence_Test extends WarehouseFunctionalTest {

    public SubSequence_Test(String testName) throws SQLException {
        super(testName);
    }


    public void testStoringSubsequence() throws SQLException {
        String sequence = "";

        DataSet dataset = new DataSet("test");
        dataset.store();

        NucleicAcid nucAcid = new NucleicAcid(dataset.getWID());
        nucAcid.setType(NucleicAcid.Type.DNA);
        nucAcid.setClass(NucleicAcid.Class.plasmid);
        nucAcid.store();

        SubSequence subSequence = new SubSequence(dataset.getWID());
        subSequence.setNucleicAcidWid(nucAcid.getWID());
        subSequence.setFullSequence(true);
        subSequence.setSequenceData(sequence);
        subSequence.store();

    }
}
